mapper.select:
	    SELECT COUNT(*)
  	    FROM answer
  	    WHERE member_idx = #{memberIdx}

mapper.delete:
DELETE 
  FROM answer
 WHERE member_idx = #{memberIdx}
   AND answer_idx IN

        #{answerIdx}

DELETE 
  FROM question 
 WHERE member_idx = #{memberIdx}
   AND question_idx IN

        #{questionIdx}
