mapper.update:
UPDATE recruit
   SET views_count = views_count + 1
 WHERE recruit_idx = #{recruit_idx}

mapper.insert:
INSERT INTO apply
	 ( member_idx
	 , recruit_idx
	 , resume_idx
	 , cl_idx
	 , apply_files_path
	 , apply_status
	 , is_viewed )
VALUES ( #{member_idx}
	 , #{recruit_idx}
     , #{resume_idx}
	 , #{cl_idx}
	 , #{apply_files_path}	
	 , #{apply_status}
	 , #{is_viewed});

mapper.update:
	    UPDATE recruit
    	SET app_count = app_count + 1
    	WHERE recruit_idx = #{recruit_idx}

mapper.select:
SELECT member_idx
  FROM member 
 WHERE member_id = #{member_id}
